package com.pcsetting.example;

public class ll {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double dnum = 160.5;
		
		System.out.println("Height is " +dnum+ "cm");
		
		System.out.println("Specified to the int type variable");
		
		int inum = (int) dnum;
		
		System.out.println("Height is " +inum+ "cm");
	}

}
